mapper.insert:
INSERT INTO CHAT_PARTICIPANT (
gcr_id, user_id, cm_id
) VALUES (
#{gcrId}, #{userId}, #{cmId}
);

mapper.select:
SELECT COUNT(*)
FROM CHAT_PARTICIPANT
WHERE gcr_id = #{gcrId}
AND user_id = #{userId}

SELECT COUNT(*)
FROM CHAT_PARTICIPANT
WHERE gcr_id = #{gcrId}

mapper.delete:
DELETE FROM CHAT_PARTICIPANT
WHERE gcr_id = #{gcrId}
AND user_id = #{userId}

mapper.update:
UPDATE CHAT_PARTICIPANT
SET cm_id = #{cmId}
WHERE gcr_id = #{gcrId}
AND user_id = #{userId}
