mapper.select:
SELECT COUNT(*) FROM REPORT
rep_status IN ('approved', 'resolved')
rep_status = #{status}

SELECT r.rep_id, r.rep_type, rc.rc_name AS category_name, u.user_name AS reporter_name, u.user_nickname, DATE_FORMAT(r.created_at, '%Y-%m-%d %H:%i:%s') AS created_at, r.rep_status, CASE WHEN r.rep_type = 'user' THEN IFNULL(target_u.user_name, '알 수 없음') WHEN r.rep_type = 'chat' THEN IFNULL(gcr.gcr_title, '알 수 없음') ELSE NULL END AS target_name, CASE WHEN r.rep_type = 'user' THEN target_u.user_nickname ELSE NULL END AS target_nickname FROM REPORT r INNER JOIN REPORT_CATEGORY rc ON r.rc_id = rc.rc_id INNER JOIN USER u ON r.user_id = u.user_id LEFT JOIN USER target_u ON r.rep_type = 'user' AND r.rep_target_id = target_u.user_id LEFT JOIN GROUP_CHAT_ROOM gcr ON r.rep_type = 'chat' AND r.rep_target_id = gcr.gcr_id
r.rc_id = #{categoryId}
ORDER BY
r.created_at ${pageRequest.sortDir}
r.rep_status ${pageRequest.sortDir}
r.created_at DESC
LIMIT #{pageRequest.size} OFFSET #{pageRequest.offset}

SELECT COUNT(*)
FROM REPORT r


        r.rc_id = #{categoryId}

SELECT r.rep_id, r.rep_type, rc.rc_name AS category_name, u.user_name AS reporter_name, u.user_nickname, DATE_FORMAT(r.created_at, '%Y-%m-%d %H:%i:%s') AS created_at, r.rep_status, CASE WHEN r.rep_type = 'user' THEN IFNULL(target_u.user_name, '알 수 없음') WHEN r.rep_type = 'chat' THEN IFNULL(gcr.gcr_title, '알 수 없음') ELSE NULL END AS target_name, CASE WHEN r.rep_type = 'user' THEN target_u.user_nickname ELSE NULL END AS target_nickname FROM REPORT r INNER JOIN REPORT_CATEGORY rc ON r.rc_id = rc.rc_id INNER JOIN USER u ON r.user_id = u.user_id LEFT JOIN USER target_u ON r.rep_type = 'user' AND r.rep_target_id = target_u.user_id LEFT JOIN GROUP_CHAT_ROOM gcr ON r.rep_type = 'chat' AND r.rep_target_id = gcr.gcr_id
r.rep_status IN ('approved', 'resolved')
r.rep_status = #{status}
AND r.rc_id = #{categoryId}
ORDER BY
r.created_at ${pageRequest.sortDir}
r.rep_status ${pageRequest.sortDir}
r.created_at DESC
LIMIT #{pageRequest.size} OFFSET #{pageRequest.offset}

SELECT COUNT(*)
FROM REPORT r



            r.rep_status IN ('approved', 'resolved')


            r.rep_status = #{status}



        AND r.rc_id = #{categoryId}

SELECT r.rep_id, r.rep_type, rc.rc_name AS category_name, u.user_name AS reporter_name, u.user_nickname, DATE_FORMAT(r.created_at, '%Y-%m-%d %H:%i:%s') AS created_at, r.rep_status, CASE WHEN r.rep_type = 'user' THEN IFNULL(target_u.user_name, '알 수 없음') WHEN r.rep_type = 'chat' THEN IFNULL(gcr.gcr_title, '알 수 없음') ELSE NULL END AS target_name, CASE WHEN r.rep_type = 'user' THEN target_u.user_nickname ELSE NULL END AS target_nickname FROM REPORT r INNER JOIN REPORT_CATEGORY rc ON r.rc_id = rc.rc_id INNER JOIN USER u ON r.user_id = u.user_id LEFT JOIN USER target_u ON r.rep_type = 'user' AND r.rep_target_id = target_u.user_id LEFT JOIN GROUP_CHAT_ROOM gcr ON r.rep_type = 'chat' AND r.rep_target_id = gcr.gcr_id
r.rep_status IN ('approved', 'resolved')
r.rep_status = #{status}
AND r.rc_id = #{categoryId}
AND ( rc.rc_name LIKE CONCAT('%', #{keyword}, '%') OR u.user_nickname LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY
r.created_at ${pageRequest.sortDir}
r.rep_status ${pageRequest.sortDir}
r.created_at DESC
LIMIT #{pageRequest.size} OFFSET #{pageRequest.offset}

SELECT COUNT(*) FROM REPORT r INNER JOIN REPORT_CATEGORY rc ON r.rc_id = rc.rc_id INNER JOIN USER u ON r.user_id = u.user_id
r.rep_status IN ('approved', 'resolved')
r.rep_status = #{status}
AND r.rc_id = #{categoryId}
